import React from 'react';
import ProductCard from '../components/ProductCard';
import '../styles/Home.css';

// Componente Home: contiene el menú lateral y los productos destacados
const Home = () => {
  const products = [
    { image: 'producto1.jpg', title: 'Producto 1', price: 10.00 },
    { image: 'producto2.jpg', title: 'Producto 2', price: 20.00 },
    // Agrega más productos aquí
  ];

  return (
    <div className="container home-container">
      <div className="row">
        {/* Menú lateral */}
        <aside className="col-md-3">
          <ul className="list-group">
            <li className="list-group-item">Categoría 1</li>
            <li class="list-group-item">Categoría 2</li>
            <li class="list-group-item">Categoría 3</li>
          </ul>
        </aside>
        {/* Productos destacados */}
        <main className="col-md-9">
          <div className="row">
            {products.map((product, index) => (
              <ProductCard
                key={index}
                image={product.image}
                title={product.title}
                price={product.price}
              />
            ))}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Home;
