import React, { useContext } from 'react';
import '../styles/MiPerfil.css';
import { GlobalContext } from '../context/GlobalState';

const MiPerfil = () => {
  const [state] = useContext(GlobalContext);
  const { user } = state;

  return (
    <div className="mi-perfil-container">
      <aside className="menu-container">
        <ul className="list-group">
          <li className="list-group-item">Mis Datos</li>
          <li className="list-group-item">Mis Compras</li>
          <li className="list-group-item">Mis Valoraciones</li>
        </ul>
      </aside>
      <main className="profile-container">
        <h1>Mi Perfil</h1>
        <div className="profile-section">
          <h2>Mis Datos</h2>
          <p><strong>Nombre:</strong> {user.nombre}</p>
          <p><strong>Apellido:</strong> {user.apellido}</p>
          <p><strong>Nickname:</strong> {user.nickname}</p>
          <p><strong>Fecha de Nacimiento:</strong> {user.fechaNacimiento}</p>
          <p><strong>Email:</strong> {user.email}</p>
        </div>
        <div className="profile-section">
          <h2>Mis Compras</h2>
          {/* Aquí puedes agregar el listado de compras */}
        </div>
        <div className="profile-section">
          <h2>Mis Valoraciones</h2>
          {/* Aquí puedes agregar el listado de valoraciones */}
        </div>
      </main>
    </div>
  );
};

export default MiPerfil;
